var remFn=function(){if(document.documentElement.clientWidth<760){document.getElementsByTagName('html')[0].style.fontSize=document.documentElement.clientWidth/7.60+"px"}else{document.getElementsByTagName('html')[0].style.fontSize="100px"}};remFn();document.createElement("header");document.createElement("footer");document.createElement("nav");document.createElement("section");document.createElement("wrap");document.createElement("data");var sizeFn=function(){var winW=document.documentElement.clientWidth;var winH=document.documentElement.clientHeight;var htmlDom=document.getElementsByTagName('html')[0];if((winW/winH)>1.7778){htmlDom.style.height="1080px";htmlDom.style.width=1080*(winW/winH)+"px";htmlDom.style.transform='scale('+winH/1080+')'}else{htmlDom.style.width="1920px";htmlDom.style.height=1920*(winH/winW)+"px";htmlDom.style.transform='scale('+winW/1920+')'}};sizeFn();if(window.addEventListener){window.addEventListener('resize',function(){remFn();sizeFn()},false)}else{window.attachEvent('onresize',function(){remFn().call(this);sizeFn().call(this)})}