var detectionData = function(str, col) {
	var color;
	switch(Math.ceil(str * 10)) {
		case 1:
			color = new echarts.graphic.LinearGradient(1, 1, 0, 0, [{
				offset: 0,
				color: col
			}, {
				offset: 0.8,
				color: '#ffffff'
			}, {
				offset: 1,
				color: '#ffffff'
			}]);
			break;
		case 2:
			color = new echarts.graphic.LinearGradient(1, 1, 0, 0, [{
				offset: 0,
				color: col
			}, {
				offset: 0.6,
				color: '#ffffff'
			}, {
				offset: 1,
				color: '#ffffff'
			}]);
			break;
		case 3:
			color = new echarts.graphic.LinearGradient(1, 1, 0, 0, [{
				offset: 0,
				color: col
			}, {
				offset: 0.6,
				color: '#ffffff'
			}, {
				offset: 1,
				color: '#ffffff'
			}]);
			break;
		case 4:
			color = new echarts.graphic.LinearGradient(0, 1, 1, 0, [{
				offset: 0,
				color: col
			}, {
				offset: 0.6,
				color: '#ffffff'
			}, {
				offset: 1,
				color: '#ffffff'
			}]);
			break;
		case 5:
			color = new echarts.graphic.LinearGradient(0, 1, 1, 0, [{
				offset: 0,
				color: col
			}, {
				offset: 0.6,
				color: '#ffffff'
			}, {
				offset: 1,
				color: '#ffffff'
			}]);
			break;
		case 6:
			color = new echarts.graphic.LinearGradient(0, 1, 1, 0, [{
				offset: 0,
				color: col
			}, {
				offset: 0.7,
				color: '#ffffff'
			}, {
				offset: 1,
				color: '#ffffff'
			}]);
			break;
		case 7:
			color = new echarts.graphic.LinearGradient(0, 1, 1, 0, [{
				offset: 0,
				color: col
			}, {
				offset: 0.8,
				color: '#ffffff'
			}, {
				offset: 1,
				color: '#ffffff'
			}]);
			break;
		case 8:
			color = new echarts.graphic.LinearGradient(0, 1, 1, 0, [{
				offset: 0,
				color: col
			}, {
				offset: 0.8,
				color: '#ffffff'
			}, {
				offset: 1,
				color: '#ffffff'
			}]);
			break;
		case 9:
			color = new echarts.graphic.LinearGradient(0, 1, 1, 1, [{
				offset: 0,
				color: col
			}, {
				offset: 0.9,
				color: '#ffffff'
			}, {
				offset: 1,
				color: '#ffffff'
			}]);
			break;
		default:
			color = new echarts.graphic.LinearGradient(0.2, 1, 0.8, 1, [{
				offset: 0,
				color: col
			}, {
				offset: 1,
				color: '#ffffff'
			}]);
	}
	return color;
}

var angleData = function(str) {
	return(135 + 270 * str) * Math.PI / 180
}
var img = new Image();
img.src = scaleplateImg;
var img2 = new Image();
img2.src = scaleplateImg2;
imgDataFn = function(val, val2) {
	(function() {
		var c = document.createElement("canvas");
		c.width = 140;
		c.height = 118;
		var ctx = c.getContext("2d");
		ctx.drawImage(img, 0, 0);
		ctx.globalCompositeOperation = "destination-in";
		ctx.arc(70, 70, 70, angleData(0), angleData(val));
		ctx.lineTo(70, 70);
		ctx.fill();
		imgData = c.toDataURL('png');
	})();
	(function() {
		var c = document.createElement("canvas");
		c.width = 140;
		c.height = 118;
		var ctx = c.getContext("2d");
		ctx.drawImage(img2, 0, 0);
		ctx.globalCompositeOperation = "destination-in";
		ctx.arc(70, 70, 70, angleData(0), angleData(val2));
		ctx.lineTo(70, 70);
		ctx.fill();
		imgData2 = c.toDataURL('png');
	})();
};

