var animateFn = function(v1, v2, vI1, vI2) {
	var val1 = 0,
		val2 = 0,
		valI1 = 0,
		valI2 = 0,
		valI1_i = vI1 / (v1 / 0.02),
		valI2_i = vI2 / (v2 / 0.02);
	var time1 = function() {
		setTimeout(function() {
			imgDataFn(val1, val2);

			gaugeChart.setOption({
				graphic: [{
						id: 'axisTick',
						shape: {
							endAngle: angleData(val1)
						},
						style: {
							stroke: detectionData(val1, '#54ffff'),
						}
					}, {
						id: 'pointer',
						rotation: -angleData(val1),
					}, {
						type: 'image',
						id: 'scaleplate',
						z: -10,
						scale: [1.14, 1.14],
						$action: 'replace',
						style: {
							width: 140,
							height: 118,
							x: 30,
							y: 54,
							opacity: 0.3,
							image: imgData,
						},
					}, {
						id: 'title',
						style: {
							text: valI1
						}
					},
					//////
					{
						id: 'axisTick2',
						shape: {
							endAngle: angleData(val2)
						},
						style: {
							stroke: detectionData(val2, '#54ffff'),
						}
					}, {
						id: 'pointer2',
						rotation: -angleData(val2),
					}, {
						type: 'image',
						id: 'scaleplate2',
						z: -10,
						scale: [1.14, 1.14],
						$action: 'replace',
						style: {
							width: 140,
							height: 118,
							x: 206,
							y: 54,
							opacity: 0.3,
							image: imgData2,
						}
					}, {
						id: 'title2',
						style: {
							text: valI2
						}
					},
				]
			});
			if(val1 < v1 || val2 < v2) {
				if(val1 < v1) {
					val1 = val1 + 0.02;
					valI1 = Math.ceil(valI1 + valI1_i);
					if(valI1 > vI1) valI1 = vI1;
				}
				if(val2 < v2) {
					val2 = val2 + 0.02;
					valI2 = Math.ceil(valI2 + valI2_i);
					if(valI2 > vI2) valI2 = vI2;
				}
				time1();
			}
		}, 30);
	};
	time1();
};

